import React, { Component } from 'react';

class ItemInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = { name: '' };
  }

  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value});
  }

  handleSubmit = (event) => {
    // alert('A form was submitted: ' + this.state);

    fetch('http://localhost:5000/api/magicitems', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(this.state)
      }).then(function(response) {
        console.log(response)
        return response.json();
      });

    event.preventDefault();
}

  render() {
    return (
      <div>
        <h1>{this.state.name}</h1>
      <form onSubmit={this.handleSubmit}>
      <input type="text" placeholder="name" name="name" />
      <input type="text" placeholder="Realm" name="realm"  />
      <input type="text" placeholder="Expires" name="expires"  />
      <input type="text" placeholder="Magical?" name="magical"  />
      {/* <input type="text" placeholder="number" name="number"  /> */}
      <input type="number" placeholder="Charges" name="charges"  />
      {/* <input type="text" placeholder="chargeLikeDetail" name="chargeLikeDetail"  /> */}
      <input type="text" placeholder="Details" name="details"  />
      <input type="text" placeholder="Written By" name="writtenBy"  />
      <input type="text" placeholder="Date Written" name="dateWritten"  />
      <input type="number" placeholder="Formal Space Total" name="formalSpaceTotal"  />
      <input type="number" placeholder="Formal Space Remaining" name="formalSpaceRemaining"  />
      <input type="text" placeholder="Last Player" name="lastplayer"  />

      <input type="submit" />
    </form>
    </div>
    );
  }
}
export default ItemInput
