import React, { useState } from "react";
import axios from "axios";
import {useParams, useHistory}  from 'react-router-dom'
import { axiosWithAuth } from "../utils/axiosWithAuth";

const initialitem = {
  item: "",
  code: { hex: "" }
};

const ItemList = ({ items, updateitems, refreshHandler }) => {
  console.log(items);
  const [editing, setEditing] = useState(false);
  const [itemToEdit, setitemToEdit] = useState(initialitem);
  const {push} = useHistory();
  const{ id } = useParams()

  const edititem = item => {
    setEditing(true);
    setitemToEdit(item);
  };

  const saveEdit = e => {
    e.preventDefault();
    // Make a put request to save your updated item
    // think about where will you get the id from...
    // where is is saved right now?
    console.log(id)
    axiosWithAuth()
      .put(`/api/items/${itemToEdit.id}`, itemToEdit)
      .then(res => {
      
        // updateitems([ ...res.data])
        // setitemToEdit() 
        setitemToEdit(res.data)
        push(`/bubble-page/`)
      })
      .catch(err => console.log(err))
      .finally(
        refreshHandler()
      )
    };

  const deleteitem = item => {
    // make a delete request to delete this item
    // item.preventDefault()
    axiosWithAuth()
      .delete(`/api/items/${item.id}`)
      .then(res => {
        push(`/bubble-page`)
      })
      .catch(err => console.log(err))
      .finally(() => {
        refreshHandler()
      })

  };

  return (
    <div className="items-wrap">
      <p>items</p>
      <ul>
        {items.map(item => (
          <li key={item.item} onClick={() => edititem(item)}>
            <span>
              <span className="delete" onClick={e => {
                    e.stopPropagation();
                    deleteitem(item)
                  }
                }>
                  x
              </span>{" "}
              {item.item}
            </span>
            <div
              className="item-box"
              style={{ backgrounditem: item.code.hex }}
            />
          </li>
        ))}
      </ul>
      {editing && (
        <form onSubmit={saveEdit}>
          <legend>edit item</legend>
          <label>
            item name:
            <input
              onChange={e =>
                setitemToEdit({ ...itemToEdit, item: e.target.value })
              }
              value={itemToEdit.item}
            />
          </label>
          <label>
            hex code:
            <input
              onChange={e =>
                setitemToEdit({
                  ...itemToEdit,
                  code: { hex: e.target.value }
                })
              }
              value={itemToEdit.code.hex}
            />
          </label>
          <div className="button-row">
            <button type="submit" onClick={(e) => {saveEdit(e)}}>save</button>
            <button onClick={() => setEditing(false)}>cancel</button>
          </div>
        </form>
      )}
      <div className="spacer" />
      {/* stretch - build another form here to add a item */}
    </div>
  );
};

export default ItemList;
