import React, { Component } from 'react';
// get items from online api
// it's return a json file
class GetOnlineitems extends Component {
    constructor(props){
        super(props);
        this.handleSubmit.bind(this)
        this.state = {
            error : null,
            isLoaded : false,
            items : []          
        };
    }
    handleSubmit(e) {
        e.preventdefault();
        1
        const data = new FormData (e.target);
        fetch('http://localhost:5000/api/magicitems', {
          method: 'POST',
          body: data,
        });
    }
    componentDidMount(){
        // I will use fake api from jsonplaceholder website
        // this return 100 items 
        fetch("http://localhost:5000/api/magicitems")
        .then( response => response.json())
        .then(
            // handle the result
            (result) => {
                this.setState({
                    isLoaded : true,
                    items : result
                });
            },

            // Handle error 
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            },
        )
    }
  
    render() {
        const {error, isLoaded, items} = this.state;

        if(error){
            return <div>Error in loading</div>
        }else if (!isLoaded) {
            return <div>Loading ...</div>
        }else{
            return(
                <div>
                    
      <form onSubmit={this.handleSubmit}>
      <input type="text" placeholder="name" name="name" />
      <select name="realm">
        <option value="Earth">Earth</option>
        <option value="Celestial">Celestial</option>
        <option value="Both">Both</option>
        <option value="Other">Other</option>
      </select>
      <input type="undefined" placeholder="Expires" name="expires"  />
      <input type="undefined" placeholder="Magical?" name="magical"  />
      {/* <input type="undefined" placeholder="number" name="number"  /> */}
      <input type="undefined" placeholder="Charges" name="charges"  />
      {/* <input type="undefined" placeholder="chargeLikeDetail" name="chargeLikeDetail"  /> */}
      <input type="undefined" placeholder="Details" name="details"  />
      <input type="undefined" placeholder="Written By" name="writtenBy"  />
      <input type="undefined" placeholder="Date Written" name="dateWritten"  />
      <input type="undefined" placeholder="Formal Space Total" name="formalSpaceTotal"  />
      <input type="undefined" placeholder="Formal Space Remaining" name="formalSpaceRemaining"  />
      <input type="undefined" placeholder="Last Player" name="lastplayer"  />

      <input type="submit" />
    </form>
                    <ol className="item">
                    {
                        items.map(item => (
                            <li key={item.id} align="start">
                                <div>
                                    <p>Name: {item.name}</p>
                                    <p>Details: {item.details}</p>
                                    <p>Realm: {item.realm}</p>
                                    <p>Expires: {item.expires}</p>
                                    <p>Written By: {item.writtenBy}</p>
                                    <p>Date Written: {item.dateWritten}</p>
                                    <p>Formal Space Total: {item.formalSpaceTotal}</p>
                                    <p>Formal Space Remaining: {item.formalSpaceRemaining}</p>
                                    <p>Last Player: {item.lastPlayer}</p>
                                    <p>Hidden/Masked Detail: {item.hiddenDetail}</p>
                                </div>
                            </li>
                        ))
                    }
                    </ol>
                </div>
            );
        }
      
    }
  }
  
  export default GetOnlineitems;
